	private void fillTable(Movie movie) {
		// TODO Auto-generated method stub
		DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
		dtm.setRowCount(0);
		listMovies = movieDao.list(movie.getName());
		if (listMovies.size() != 0) {
			for (int i = 0; i < listMovies.size(); i++) {
				movie = listMovies.get(i);
				Vector v = new Vector();
				v.add(movie.getId());
				v.add(movie.getName());
				//v.add(movie.getIntroduce());
				v.add(movie.getIndate());
				//v.add(movie.getOutdate());
				v.add(movie.getTime());
				//v.add(movie.getDirector());
				//v.add(movie.getStar());
				v.add(movie.getStyle());
				v.add(movie.getKind());
				dtm.addRow(v);
			}
		}

	}

