	private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {
		// TODO add your handling code here:
		// TODO add your handling code here:
		// TODO add your handling code here:
		int row = jTable1.getSelectedRow();//取得表格选中行
		if(row>-1){
			String movie_id = jTable1.getValueAt(row, 0).toString();
			int n = JOptionPane.showConfirmDialog(null, "确定要删除这条记录吗？");
			if (n == 0) {
				Movie movie = new Movie();
				movie.setId(movie_id);
				int delecteNum = movieDao.delete(movie);
				if (delecteNum == 1) {
					JOptionPane.showMessageDialog(null, "删除成功！");
					this.fillTable(new Movie());
				} else {
					JOptionPane.showMessageDialog(null, "删除失败！");
				}
			}
		}else{
			JOptionPane.showMessageDialog(null, "请选择你要删除的记录！");
			return;
		}
	}

